mapper.select:
   select r.reservation_id, r.reservation_date,m.name,s.status
from reservation r, member m, payment_status s



        m.name like #{pattern1}
        or s.status like #{pattern1}
        or r.reservation_date like #{pattern1}

      and r.member_id = m.member_id
and r.status_id = s.status_id



      limit #{rowNo}, #{size}

mapper.update:
update reservation
name = #{name}, email = #{email}, password = password(#{password}), nickname = #{nickname}, birth = #{birth}, sms_check = #{smsCheck}, email_check = #{emailCheck}, tel = #{tel}, rank = #{rank}, phone_check = #{phoneCheck}, photo = #{photo}
where reservation_id = #{no}

mapper.select:
select count(*)
from reservation



    name like #{pattern1}
    or email like #{pattern1}
    or tel like #{pattern1}
